from Crypto.PublicKey import RSA
from Crypto.Math.Numbers import Integer
from Crypto.Util import number

def generate_rsa_key(bits, p, q):
    n = p * q
    e = 65537
    phi = (p - 1) * (q - 1)
    d = int(Integer(e).inverse(Integer(phi)))

    key = RSA.construct((n, e, d, p, q))
    return key

